#!/bin/bash

set -e

# Make sure we're deploying to the local network
export DFX_NETWORK=local
MINTER_IDENTITY="minter"

# If the following command fails, it means that we don't have the minter identity,
# so we return an error explaining the action to take
MINTER_PRINCIPAL=$(dfx identity --identity $MINTER_IDENTITY get-principal) || {
  echo "Error: Minter identity not found. Please create one using 'dfx identity new $MINTER_IDENTITY'"
  exit 1
}

MINTER_ACCOUNT_ID=$(dfx ledger --identity $MINTER_IDENTITY account-id)

# Deploy the dependencies
dfx deps deploy

# Deploy the ledgers
COMMON_ICRC_INIT_ARGS="
  minting_account = record { owner = principal \"$MINTER_PRINCIPAL\"; };
  initial_balances = vec {};
  archive_options = record {
    num_blocks_to_archive = 1000;
    trigger_threshold = 2000;
    max_message_size_bytes = null;
    cycles_for_archive_creation = opt 100_000_000_000_000;
    node_max_memory_size_bytes = opt 3_221_225_472;
    controller_id = principal \"2vxsx-fae\"
  };
  max_memo_length = opt 80;
  feature_flags = opt record { icrc2 = true };
"

# ICP
dfx deploy icp-ledger --argument "(variant {
  Init = record {
    send_whitelist = vec {};
    token_symbol = opt \"ICP\";
    token_name = opt \"ICP\";
    transfer_fee = opt record { e8s = 10_000 : nat64; };
    minting_account = \"$MINTER_ACCOUNT_ID\";
    icrc1_minting_account = opt record { owner = principal \"$MINTER_PRINCIPAL\"; };
    initial_values = vec {};
    feature_flags = opt record { icrc2 = true };
  }
})"

# ckBTC
dfx deploy ckbtc-ledger --argument "(variant {
  Init = record {
    decimals = opt 8;
    token_symbol = \"ckBTC\";
    token_name = \"ckBTC\";
    transfer_fee = 10;
    metadata = vec {
      record {
        \"icrc1:logo\";
        variant {
          Text = \"data:image/svg+xml;base64,\"
        };
      };
    };
    $COMMON_ICRC_INIT_ARGS
  }
})"

# ckETH
dfx deploy cketh-ledger --argument "(variant {
  Init = record {
    decimals = opt 18;
    token_symbol = \"ckETH\";
    token_name = \"ckETH\";
    transfer_fee = 2_000_000_000_000;
    metadata = vec {
      record {
        \"icrc1:logo\";
        variant {
          Text = \"data:image/svg+xml;base64,\"
        };
      };
    };
    $COMMON_ICRC_INIT_ARGS
  }
})"

# ckUSDC
dfx deploy ckusdc-ledger --argument "(variant {
  Init = record {
    decimals = opt 6;
    token_symbol = \"ckUSDC\";
    token_name = \"ckUSDC\";
    transfer_fee = 10_000;
    metadata = vec {
      record {
        \"icrc1:logo\";
        variant {
          Text = \"data:image/svg+xml;base64,\"
        };
      };
    };
    $COMMON_ICRC_INIT_ARGS
  }
})"

# Deploy the backend
dfx deploy backend
# Set the default wheel assets
dfx canister call backend set_default_wheel_assets

BACKEND_CANISTER_ID=$(dfx canister id backend)

# Transfer 100 ICP (8 decimals) to the backend
dfx canister --identity $MINTER_IDENTITY call icp-ledger icrc1_transfer "(record {to = record { owner = principal \"$BACKEND_CANISTER_ID\"; }; amount = 10_000_000_000; })"

# Transfer 10 ckBTC (8 decimals) to the backend
dfx canister --identity $MINTER_IDENTITY call ckbtc-ledger icrc1_transfer "(record {to = record { owner = principal \"$BACKEND_CANISTER_ID\"; }; amount = 1_000_000_000; })"

# Transfer 10 ckETH (18 decimals) to the backend
dfx canister --identity $MINTER_IDENTITY call cketh-ledger icrc1_transfer "(record {to = record { owner = principal \"$BACKEND_CANISTER_ID\"; }; amount = 10_000_000_000_000_000_000; })"

# Transfer 100 ckUSDC (6 decimals) to the backend
dfx canister --identity $MINTER_IDENTITY call ckusdc-ledger icrc1_transfer "(record {to = record { owner = principal \"$BACKEND_CANISTER_ID\"; }; amount = 100_000_000; })"

# Update the tokens data in the backend
dfx canister call backend fetch_tokens_data
